mapper.insert:
INSERT INTO T_COIN_PUMP_DAY_KRW
(
    MARKET
    , OPENING_PRICE
    , HIGH_PRICE
    , LOW_PRICE
    , RISE_PRICE
    , PUMP_SET_RISE_PRICE
    , UPD_DT
)

    SELECT
        #{item.MARKET}
        , #{item.OPENING_PRICE}
        , #{item.HIGH_PRICE}
        , #{item.LOW_PRICE}
        , #{item.RISE_PRICE}
        , #{item.PUMP_SET_RISE_PRICE}
        , TO_CHAR(TO_DATE(#{item.UPD_DT}, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS')
    FROM DUAL
